const locales = [
    {
      code: 'en',
      file: 'en.json',
      name: 'English'
    },
    {
      code: 'zh',
      file: 'zh.json',
      name: '简体中文'
    },
    {
      code: 'ja',
      file: 'ja.json',
      name: '日本語'
    },
    {
      code: 'ko',
      file: 'ko.json',
      name: '한국어'
    },
    {
      code: 'fr',
      file: 'fr.json',
      name: 'Français'
    }
  ]

  export const currentLocales = locales
  export const currentLocaleCodes = locales.map(l => l.code)

import en from './locales/en.json'
import zh from './locales/zh.json'
import { currentLocales } from './locales/config'
// import { defineI18nConfig } from '@nuxt/schema'

export default defineI18nConfig(() => ({
  legacy: false,
  locale: 'en',
  availableLocales: currentLocales.map(l => l.code),
  fallbackLocale: 'en', // 区配不到的语言就用en
  messages: {
    en,
    zh,
  }
}))
